    margin-top: 0;
  }

  h3 {
    font-size: 1.75rem;
    font-weight: 600;
    padding-bottom: .5rem;
    margin-top: 2rem;
  }

  h4 {
    font-size: 1.25rem;
    font-weight: 600;
    line-height: 1;
    margin-bottom: .5rem;
  }

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
  }

  p {
    line-height: 1.6;
    font-size: 1.125rem;
    font-weight: 300;
    &:not(:last-child) {
      margin-bottom: 1rem;
    }
