{#if banner.label}
  <div class="banner">
    <div class="memo-content">
      <p>{@html banner.label}</p>
      {#if banner.cta.label}
        <a href={banner.cta.url}>{banner.cta.label}</a>
      {/if}
    </div>
  </div>
{/if}
<header class="section-container">
  <a href="/" class="logo">
    <span>{logo.title}</span>
    {@html logo.svg}
  </a>
  <nav>
    <a
      class="link pill"
      href="https://github.com/primocms/primo"
      aria-label="Github repo">
      <Icon icon="ic:outline-star-purple500" />
      <span>Star on Github</span>
    </a>
    {#each nav as { link, links, featured }}
      {@const hasDropdown = links.length > 0}
      <div class="nav-item">
        <div class="top-link">
          {#if featured}
            <span class="featured-pill"> New </span>
          {/if}
          {#if hasDropdown}
            <span>{link.label}</span>
            <span class="icon"> <Icon icon="akar-icons:chevron-down" /></span>
          {:else}
            <a
              href={link.url}
              class:active={link.url === window.location.pathname}
              class="link">{link.label}</a>
          {/if}
        </div>
        {#if hasDropdown}
          <div class="dropdown">
            {#each links as { link }}
              <a href={link.url} class="link">{link.label}</a>
            {/each}
          </div>
        {/if}
      </div>
    {/each}
    <button id="open" on:click={toggleMobileNav}>
     <div class="menu-icon"><Icon icon="eva:menu-outline" /></div>
    </button>
  </nav>
  {#if mobileNavOpen}
    <nav id="mobile-nav" transition:fade={{ duration: 200 }}>
      {#each nav as { link, links }}
        {@const hasDropdown = links.length > 0}
        {#if hasDropdown}
          {#each links as { link }}
            <a href={link.url} class="link">{link.label}</a>
          {/each}
        {:else}
          <a href={link.url} class="link">{link.label}</a>
        {/if}
      {/each}
      <button
        on:click={toggleMobileNav}
        id="close"
        aria-label="Close Navigation">
        <Icon icon="bi:x-lg" />
      </button>
    </nav>
  {/if}
</header>

