@import url("https://unpkg.com/tailwindcss@2.2.15/dist/base.css");
@import url(https://fonts.bunny.net/css?family=archivo:200,300,300i,400,400i,500,500i,600,600i,700,700i);

#page {
  --color-primored: #35d994;
  --color-gray: #262626;
  --color-black: #121212;
  --color-white: #ffff;
  --color-accent: var(--color-primored);
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.1);

  --padding-left: 2rem;
  --padding-right: 2rem;

  --color: #F3F3F3;
  --background: var(--color-black);
  --max-width: 1200px;
  --border-color: var(--color-gray);
  --border: 2px solid var(--border-color);
  --border-radius: 5px;

  --heading-font-size: clamp(1rem, 10vw, 2.25rem);
  --heading-color: #FDFDFD;
  --heading-font-weight: 600;
  --heading-line-height: 1.3;
  --subheading-color: #DADADA;
  --body-color: #CECECE;
  --heading-line-height: 1.3;

  --transition-time: 0.1s;
  --transition: var(--transition-time) color,
    var(--transition-time) background-color, var(--transition-time) background,
    var(--transition-time) border-color,
    var(--transition-time) text-decoration-color,
    var(--transition-time) box-shadow, var(--transition-time) transform;

  --button-color: white;
  --button-background: var(--background);
  --button-border: 1px solid white;
  --button-hover-background: var(--color-primored);
  --button-padding: 8px 20px;
  --button-border-radius: 5px;

  --padding: 2rem;

  color: var(--body-color);
  background: var(--background);
  font-family: "Inter", system-ui, sans-serif;
  transition: var(--transition);

  &[lang="ar"] {
    direction: rtl;
  }
  font-weight: 300;
  font-size: 1.125rem;
  line-height: 1.2;
}

.section-container {
  max-width: var(--max-width);
  margin: 0 auto;
  padding-left: var(--padding);
  padding-right: var(--padding);
  padding-top: 5rem;
  padding-bottom: 5rem;
}

.button {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.25rem;
  color: var(--button-color);
  border: var(--button-border);
  border-radius: 6px;
  padding: var(--button-padding);
  transition: var(--transition);
  transform: translateY(0);
  font-weight: 400;

  &.secondary {
    border: 0;
    color: var(--color-white);
    background: var(--color-gray);
  }

  &.is-primary {
    border: 1.5px solid var(--color-accent);
  }

  &.small {
    color: var(--color);
    padding: 0.25rem 0.75rem 0.25rem 0.25rem;
    font-size: 0.75rem;
    background: transparent;
    border: 1.5px solid var(--border-color);
    display: flex;
    margin-bottom: 1rem;

    svg {
      width: 1rem;
      height: 1rem;
    }
  }

  &:hover {
    border-color: transparent;
    background: var(--color-background);
    color: white;
    box-shadow: 0 0 0 2px var(--color-accent);
  }

  &.disabled {
    cursor: disabled;
    opacity: 0.5;
    pointer-events: none;
  }
}

.heading {
  font-family: "Archivo", system-ui, sans-serif;
  font-size: var(--heading-font-size, 49px);
  line-height: var(--heading-line-height, 1.15);
  font-weight: var(--heading-font-weight, 500);
  color: var(--heading-color, #252428);
  text-align: center;
}

h2.heading {
  line-height: 1.15;
  font-size: 2.5rem;
}

.subheading {
  text-align: center;
  font-size: 1.125rem;
}

.link {
  transition: border-color 0.1s;

  &:hover {
    border-color: transparent;
  }

  &.underlined {
    border-bottom: 2px solid var(--color-accent, #154bf4);

    &:hover {
      border-color: transparent;
    }
  }

  &.arrow {
    display: inline-flex;
    align-items: center;
    color: var(--color-accent);
    font-weight: 500;

    svg {
      transition: 0.1s transform;
      transform: translateX(0);
      position: relative;
      top: 2px;
    }

    span {
      margin-right: 0.5rem;
    }

    &:hover svg {
      transform: translateX(4px);
    }
  }
}

.content {

  blockquote {
    padding: 2rem;
    border-left: 5px solid var(--border-color);
    margin: 2rem 0;
    font-size: 1.5rem;
  }

  h1 {
    font-family: "Archivo", system-ui, sans-serif;
    color: var(--color);
    font-size: 2.5rem;
    font-weight: 600;
    margin-bottom: 1rem;
    font-weight: 700;
    letter-spacing: -0.01em;
  }

  h2 {
    font-family: "Archivo", system-ui, sans-serif;
    color: var(--color);
    font-size: 2.25rem;
    font-weight: 600;
    line-height: 1.3;
    margin-top: 3rem;
    margin-bottom: 1rem;
  }

  h2 + h3 {
    margin-top: 0;
  }

  h3 {
    font-size: 1.75rem;
    font-weight: 600;
    padding-bottom: .5rem;
    margin-top: 2rem;
  }

  h4 {
    font-size: 1.25rem;
    font-weight: 600;
    line-height: 1;
    margin-bottom: .5rem;
  }

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
  }

  p {
    line-height: 1.4;
    font-size: 1.125rem;
    font-weight: 300;
    &:not(:last-child) {
      margin-bottom: 1rem;
    }
  }

  ol {
    list-style: decimal;
    padding-left: 1.25rem;
  }

  ul {
    list-style: disc;
    padding-left: 1.25rem;
    margin-bottom: 1rem;

    li {
      padding: 0.25rem 0;
    }
  }

  strong {
    font-weight: 500;
    color: #35
  }

  hr {
    border-color: var(--border-color); 
    margin-block: 5rem; 
  }

  a {
    border-bottom: 2px solid var(--color-accent);

  }
}

form {
  display: grid;
  gap: 1rem;

  label {
    display: grid;
    gap: 0.25rem;

    span {
      font-size: 0.75rem;
      font-weight: 500;
    }

    input,
    textarea {
      padding: 0.5rem;
      outline-color: transparent;
      color: #222;
      transition: var(--transition, 0.1s outline-color);
      border: 1.5px solid var(--border-color, #eee);
      border-radius: var(--border-radius);

      &:focus {
        outline-color: var(--color-accent, rebeccapurple);
      }

      &::placeholder {
        font-weight: 300;
      }
    }
  }

  .button {
    margin-top: 0.5rem;
    display: flex;
    justify-content: center; 
    font-weight: 400;
  }
}

.content {
  text-align: center !important; 
}

.section-container {
  display: grid;
  gap: 4rem;
  padding-block: 2rem;
}

.heading-group {
  display: grid;
  gap: 0.5rem;
  place-content: center;
  text-align: center;

  .superhead {
    font-family: var(--font-body);
    color: var(--color-accent);
    font-size: 0.875rem;
    font-weight: 500;
    letter-spacing: 1.5px;
    text-transform: uppercase;
  }

  .subheading {
    color: #4f6373;
    line-height: 1.4;
    max-width: 600px;
    font-weight: 400;
    max-width: 600px;
    margin: 0 auto;
  }
}

ul {
  display: grid;
  place-content: center;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 4rem;

  li {
    display: grid;
    place-content: start;
    gap: 2rem;

    .quote {
      font-size: 1.125rem;
      line-height: 1.5;
    }

    .person {
      display: flex;
      gap: 1rem;

      img {
        width: 40px;
        height: 40px;
        object-fit: cover;
        border-radius: 50%;
      }

      .text {
        display: grid;
        gap: .125rem;

      }

      .name {
        font-weight: 600;
      }

      .subtitle {
        font-weight: 500;
        font-size: 0.875rem;
      }
    }
  }
}

.section-container {
  padding-bottom: 0;
}

.heading {
  padding-bottom: 4px;
  text-align: left;
}

