  {
    id: "packs",
    heading: "Component Packs",
    items: packs,
  },
  {
    id: "components",
    heading: "Components",
    items: components,
  },
];

let active_index = 0;
$: active_tab = tabs[active_index];
$: active_items = active_tab.items.filter(i => i.available);

function set_tab(i) {
  active_index = i;
}

let active_item;
let active_item_index;
function set_item(item, index = null) {
  active_item = item;
  active_item_index = index;
}

function show_previous_item() {
  active_item_index--;
  active_item = active_items[active_item_index];
}
function show_next_item() {
  active_item_index++;
  active_item = active_items[active_item_index];
}

let cart = [];
$: total_cost = cart.reduce((a, b) => a + Number(b.price), 0);

get("cart").then((val) => {
  if (val) cart = val;
});
$: set("cart", cart);

let showing_cart = false;
function add_to_cart(item) {
  cart = [...cart, item];
}

function remove_from_cart(item) {
  cart = cart.filter((i) => i.name !== item.name);
}

const downloaded = {};
async function fetch_and_download({ name, repo, screenshots }) {
  // mixpanel.track('Download', { name })

  downloaded[screenshots.desktop.url] = true;

  console.log('downloading')
  let component_id;
  // if (download_url.includes("#")) {
  //   download_url = preview_url.substr(0, preview_url.indexOf("#"));
  //   component_id = preview_url.substr(preview_url.indexOf("#") + 1);
  // }
  // download_url = download_url.replace(/\/$/, "")
  const { data } = await axios.get(`https://raw.githubusercontent.com/${repo}/main/primo.json`);

  let file_to_save;
  if (component_id) {
    const {symbolID} = find(data.pages[0]['sections'], ['id',component_id])
    const symbol = find(data.symbols, ['id', symbolID])
    file_to_save = new Blob([JSON.stringify(symbol)], {
      type: "application/json",
    });
  } else {
    file_to_save = new Blob([JSON.stringify(data)], {
      type: "application/json",
    });
  }

  // Save the file
  fileSaver.saveAs(file_to_save, name);
}

