.section-container {
  padding-top: 3rem;
}

header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 3rem;
  margin-bottom: 2rem;

  .heading {
    text-align: left;
    margin-bottom: 0.75rem;
  }

  .subheading {
    color: #eee;
    text-align: left;
    max-width: 675px;
  }

  button {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    border-radius: var(--border-radius);
    border: 1px solid var(--color-accent);
    padding: 0.25rem 0.75rem;
  }
}

/* .tabs {
  display: flex;
  flex-wrap: wrap;
  gap: 2rem;
  margin-bottom: 2.5rem;
  border-bottom: 1px solid var(--border-color);

  button {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    transition: 0.1s background, 0.1s border-color;
