} */

.items {
  display: grid;
  gap: 2rem;
  place-items: start;
  @media (min-width: 500px) {
    grid-template-columns: 1fr 1fr;
  }
  @media (min-width: 700px) {
    grid-template-columns: 1fr 1fr 1fr;
  }
}

.item {
  display: grid;

  img {
    margin-bottom: 0.75rem;
    border-radius: 0.125rem;
    overflow: hidden;
    aspect-ratio: 1.75;
    object-fit: cover;
    object-position: top;
  }

  footer {
    display: flex;
    align-items: center;
    justify-content: space-between;
    flex-wrap: wrap;
    gap: 0.75rem;
    font-size: 1.125rem;

    .info {
      font-size: 16px;
      display: flex;
      align-items: center;
      gap: 0.5rem;

      .price {
        display: flex;
        align-items: center;
        font-size: 14px;
        border-radius: 1rem;
        background: #333;
        padding-inline: 8px;
        margin: auto 0;
      }
    }

    .buttons {
      display: flex;
      gap: 0.45rem;
      button {
        transition: 0.1s color;
        &:hover {
          color: var(--color-accent);
        }
      }

      button:last-child {
        font-size: 1.2rem;
      }
    }
  }
}

#overlay {
  position: fixed;
  inset: 0;
  width: 100vw;
  background: rgba(0, 0, 0, 0.9);

  .background {
    position: absolute;
    inset: 0;
    width: 100%;
    height: 100%;
  }

  .inner {
    position: absolute;
    right: 0;
    width: 100%;
    max-width: 400px;
    height: 100%;
    background: white;
    padding: 2rem;
    display: grid;
    gap: 1rem;
    place-content: space-between normal;
  }

  .summary {
  }

  header {
    width: 100%;
    justify-content: space-between;
    display: flex;

    button {
      color: #111;
      border: 0;
    }
  }

  .heading {
    color: #222;
    font-size: 1.5rem;
    text-align: left;
  }

  ul {
    display: grid;
    gap: 1.5rem;
  }

  li {
    display: grid;
    gap: 0.5rem;

    img {
      object-fit: contain;
      border-radius: 0.25rem;
      border: 1px solid #eee;
    }
    h3 {
      font-weight: 500;
    }
    .info {
      color: #555;
      display: flex;
      gap: 0.5rem;

      .price {
        color: #999;
      }
    }
    button {
      margin-left: auto;
      display: flex;
      align-items: center;
      gap: 0.25rem;
      font-size: 0.875rem;
    }
  }

  .cta form {
    display: grid;
    gap: 0.5rem;
    .total {
      font-size: 2rem;
      font-weight: 600;
      color: #111;
    }
    input {
      display: none;
    }
    .button {
      justify-content: center;
      width: 100%;

      &:hover {
        color: #111;
      }
    }
  }
}

#modal {
  position: fixed;
  inset: 0;
  z-index: 1;
  display: flex;
  justify-content: center;
  backdrop-filter: blur(6px);
  background: rgba(0, 0, 0, 0.9);

  .loading {
    width: 100%;
    font-size: 5rem;
    display: flex;
    justify-content: center;
  }

  .background {
    position: absolute;
    inset: 0;
    width: 100%;
    height: 100%;
  }

  button.close {
    font-size: 1.5rem;
    color: white;
    position: absolute;
    top: 1rem;
    left: 1rem;
  }

  .arrow-buttons {
    position: fixed;
    inset: 0;
    padding: 0;
    display: flex;
    justify-content: space-between;
    align-items: center;
    pointer-events: none;
    button {
      pointer-events: all;
      padding: 0.5rem;
      border-radius: 50%;
      background: #222;
      transition: 0.1s background;

      &:hover {
        background: #333;
      }
    }
    button.left {
      margin-right: auto;
    }
    button.right {
      margin-left: auto;
    }
    @media (min-width: 1200px) {
      button {
        font-size: 2rem;
      }
      button.left {
        margin-left: -1.5rem;
      }
      button.right {
        margin-right: -1.5rem;
      }
    }
  }

  .inner {
    width: 100%;
    display: flex;
    flex-direction: column;
    background: #111;
    border-radius: var(--border-radius);
    border: 1px solid var(--border-color);
    position: relative;
    margin: 1rem;
    overflow: scroll;

    header {
      display: grid;
      gap: 4rem;
      margin-bottom: 8rem;

      .body {
        display: grid;
        place-content: start;

        .price {
          background: #222;
          padding: 0 0.75rem;
          border-radius: 1rem;
          margin-bottom: 0.5rem;
          margin-right: auto;
        }

        .heading {
          text-align: left;
          margin-bottom: 1rem;
        }

        .content {
          margin-bottom: 1.75rem;
          padding: 0;
        }

        .actions {
          display: flex;
          gap: 1rem;
        }
      }

      figure {
        position: relative;
        img {
          border-radius: var(--border-radius);
        }
        img.desktop {
          width: 100%;
        }
        img.mobile {
          width: 30%;
          position: absolute;
          bottom: -2rem;
          right: 2rem;
          box-shadow: var(--box-shadow);
        }
      }

      @media (min-width: 900px) {
        grid-template-columns: 1fr 1fr;
        gap: 7rem;
      }
    }

    section {
      display: grid;
      gap: 2rem;

      h2 {
        font-weight: 600;
        font-size: 1.75rem;
        border-bottom: 1px solid var(--border-color);
        padding-bottom: 1rem;
      }

      .screenshots {
        display: flex;
        flex-direction: column;
        gap: 2rem;
        padding-bottom: 6rem;

        img:first-child {
          width: 30%;
          border-radius: 4px;
        }
        img:nth-child(2) {
          width: 60%;
          border-radius: 4px;
        }
        img:last-child {
          width: 100%;
          border-radius: 4px;
        }
      }

      ul {
        display: grid;
        gap: 2rem;
        padding-bottom: 6rem;

        li {
          display: flex;
          gap: 0.5rem;

          :global(a) {
            text-decoration: underline;
          }

          .icon {
            font-size: 1.25rem;
            color: var(--color-accent);
            margin-top: 2px;
          }
        }
      }

      .accordion {
        li {
          display: grid;
          text-align: left;
          .title {
            text-align: left;
            font-weight: 600;
            display: flex;
            justify-content: space-between;
            gap: 0.5rem;
            width: 100%;
            transition: 0.1s color;

            .icon {
              color: inherit;
              transition: 0.1s transform;
            }
          }

          &.active {
            .title {
              color: var(--color-accent);

              .icon {
                transform: scaleY(-1);
              }
            }
          }
        }
      }
      @media (min-width: 900px) {
        grid-template-columns: 1fr 2fr;
        gap: 7rem;

        h2 {
          padding-right: 3rem;
          border-bottom: 0;
          border-right: 1px solid var(--border-color);
        }
      }
    }

    @media (min-width: 900px) {
      padding: 3rem;
      padding-top: 4rem;
    }
  }

  &.completed-purchase {
    header {
      display: grid;
      grid-template-columns: auto;
      place-items: start;
      place-content: start;
      gap: 0.5rem;

      .heading {
        text-align: left;
        margin-bottom: 0.5rem;
      }

      .subheading {
        text-align: left;
        margin-bottom: 1rem;
      }

      button {
        padding: 0.5rem 1.125rem;
        border-color: var(--color-accent);
      }
    }

    ul {
      display: grid;
      margin-bottom: 3rem;

      li {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 4rem;
        padding: 3rem 0;
        border-bottom: 1px solid var(--border-color);
      }
    }

    h3 {
      font-size: 1.5rem;
      font-weight: 600;
      margin-bottom: 1rem;
    }

    button {
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .instructions {
      margin-bottom: 3rem;
      :global(h3) {
        font-size: 1.5rem;
        font-weight: 600;
        margin-bottom: 1rem;
      }
      :global(ol) {
        display: grid;
        gap: 0.5rem;
        list-style: decimal;
        padding-left: 1.5rem;
        margin-bottom: 2rem;
      }
    }
  }
}

.section-container {
  padding-block: 0;
}

iframe {
  width: 100%;
  aspect-ratio: 1.69;
}

.content {
  :global(h1) {
    text-align: var(--heading-align);
  }
  :global(h2 + h3) {
