let loading = false;
let submitted = false;
let error = false;

async function submit_form(e) {
  loading = true;

  const myForm = e.target;
  const formData = new FormData(myForm);

  fetch("/", {
    method: "POST",
    headers: { "Content-Type": "application/x-www-form-urlencoded" },
    body: new URLSearchParams(formData).toString(),
  })
    .then(() => {
      submitted = true;
      loading = false;
    })
    .catch((error) => {
      loading = false;
      alert(error);
    });
}

