let parent_page = {}

function get_title(page) {
  return page.sections[1]["content"]["en"]["heading"] || null
}

function get_description(page) {
  return page.sections[1]["content"]["en"]["content"] ? page.sections[1]["content"]["en"]["content"] .html : null
}

async function get_pages() {
  const data = await axios.get("/primo.json").then((res) =>{
    console.log(res)
    return  res.data
  })
  console.log({data})
  parent_page = data.pages.find((page) => page.name === root_page);

  // console.log(data);
  if (root_page) {
    const child_pages = data.pages
      .filter((page) => page.parent === parent_page.id)
      .map((page) => ({
        ...page,
        sections: data.sections
          .filter((section) => section.page === page.id)
          .sort((a, b) => a.index - b.index),
      }));

    // console.log({ child_pages });

    return child_pages;
  } else {
    return data.pages;
  }
}

